  $("#hard-suggestions").hide();
  $("#ajax-s-list").show();
}

if(value != " "){
   $.ajax({
       type:"get",
        dataType:"json",
        url:'{{url("acomplete")}}',
        data: {
          search : value,
        },
        success:function(data){
          if(!data){
            alert("no result");
             
          }else{
             var temp ="";
              $.each(data, displayResults);
              function displayResults(item, index) {
              temp += index;
              // document.getElementById("ajax").innerHTML = temp;
            }
          }
          $("filters").show();
            document.getElementById("ajax-s-list").innerHTML = temp;
             
        }
    }).fail(function(jqXHR, textStatus, error){
        alert(jqXHR.responseText);
    });



  if(event.keyCode == 13){
